export default function Avatar({ name, picture }) {
    return (
        <div className="avatarWrap">
            <img src={picture} className="headImg" alt={name} />
            <div className="ArticleAvatarInfo">
                <div className="nameInfo">{name}</div>
                <div>
                    <span>2020.11.06 15:50:09</span>
                    <span>留言 200</span>
                    <span>阅读 300</span>
                </div>
            </div>
            <style jsx>
                {
                    `
                        .nameInfo{
                            font-size: 16px;
                            font-weight: 500;
                            margin-right: 8px;
                        }
                        .avatarWrap{
                            display: flex;
                            align-items: center;
                        }
                        .headImg{
                            display: block;
                            border-radius: 50%;
                            border: 1px solid #eee;
                            min-width: 48px;
                            min-height: 48px;
                            width: 48px;
                            height: 48px;
                        }
                        .ArticleAvatarInfo{
                            margin-left: 8px;
                            font-size: 13px;
                            color: #404040;
                        }
                    ` 
                }
            </style>
        </div>
    )
}
