import styles from '../../styles/articleTrees.module.scss';
import { Tree } from 'antd';
import { isEmpty } from 'zgl-utils-js';
import React, { useState } from 'react';


const { DirectoryTree } = Tree;

export default function ArticleTrees ({com_class_list}) {

    const  com_classList = !isEmpty(com_class_list) && com_class_list.map(item => {
         if (item.isLeaf) {
             return {
                 title: item.name,
                 key: item.value
             }
         } else {
             return {
                 title: item.name,
                 key: item.value,
                 children: this.getItemChild(item.children)
             }
         }
     })
     const [treeData, setTreeData] = useState(com_classList)

    getItemChild = (item) => {
        return item.map(child => {
            if (child.isLeaf) {
                return {
                    title: child.name,
                    key: child.value
                }
            } else {
                return {
                    title: child.name,
                    key: child.value,
                    children: this.getItemChild(child.children)
                }
            }
        })
    }
    updateTreeData = (list, key, children) => {
        return list.map((node) => {
          if (node.key === key) {
            return { ...node, children };
          } else if (node.children) {
            return { ...node, children: updateTreeData(node.children, key, children) };
          }

          return node;
        });
    }
    onSelect = (keys, event) => {
        console.log('Trigger Select', keys, event);
    };

    onExpand = () => {
        console.log('Trigger Expand');
    };
    onLoadData = ({ key, children }) => {
            return new Promise((resolve) => {
                if (children) {
                    resolve();
                    return;
                }

                setTimeout(() => {
                    setTreeData((origin) =>
                        updateTreeData(origin, key, [
                            {
                                title: 'Child Node',
                                key: `${key}-0`,
                            },
                            {
                                title: 'Child Node',
                                key: `${key}-1`,
                            },
                        ]),
                    );
                    resolve();
                }, 1000);
            });
        }


    return (
        <div className={styles.trees}>
            <div className={styles.visitorTitle}> 
                分类列表 
            </div>
            <DirectoryTree
                multiple
                loadData={onLoadData}
                onSelect={() => this.onSelect()}
                onExpand={() => this.onExpand()}
                treeData={this.state.com_classList}
            />
        </div>
    )
}
