/**
 * 页面名_模块名_类型名
 */
const postState = {
    post_article_list: (action) => {
        console.log(action, 'action');
        return {
            default: {},
            type: 'postArticleList',
            data: {
                post_article_list: action? action.data: {}
            }
        }
    }
}

export default postState;
