import Link from 'next/link';
import imgs from '../images/images';
import styles from '../styles/header.module.scss';
import http from '../http';

const Header = (props) => {
    return (
        <header className={styles.header}>
            <div>
                <img className={styles.headLogoImg} src={ imgs.headerLogo } />
            </div>
            <div className={styles.sortList}>
                    {props.sortList.map(show => {
                        return (
                            <li key={show.value}>
                                <Link as={`/load/${show.value}`} href={`/post?id=${show.value}`}>
                                    <span>{show.name}</span>
                                </Link>
{/* 
                                <Link href="/">
                                    <span>Home</span>
                                </Link>
                                <Link href="/about">
                                    <span>About</span>
                                </Link> */}
                            </li>
                        );
                    })}
            </div>
        </header>
    )
}

Header.getInitialProps = async (context) => {
    console.log(context, 'contextcontext');
    const data = await http({ url: '/sort/getSortList', method: 'get' , params: {}})
    console.log(data, '==');
    return {
        shows: data.data
    }
}

export default Header;
