    com_label_integer: (action) => {
        return {
            default: 0,
            type: 'comLabelInteger',
            data: {
                com_label_integer: Number(action? action.com_label_integer: 0)
            }
        }
    },
    com_sort_list: (action) => {
        return {
            default: 0,
            type: 'comSortList',
            data: {
                com_sort_list:  action? action.data: {}
            }
        }
    },
    com_user_info: (action) => {
        httpAgent({url: '/user/getUserInfo', method: 'post' , params}).then(res => {
            this.setState({
                visible: false
            })
        })
        return {
            default: {
                agent: 'adadad',
                nickname: 'poippo'
            },
            type: 'comUserInfo',
            data: {
                com_user_info: {}
            }
        }
    }
}

export default comState;
