import { Modal, Button, Input } from "antd";
import { ExclamationCircleOutlined } from "@ant-design/icons";
import React from 'react';

class LocalizedModal extends React.Component {
    constructor() {
        super(props)
    }

    state = { visible: false };

    showModal = () => {
        this.setState({
            visible: true,
        });
    };

    hideModal = () => {
        this.setState({
            visible: false,
        });
    };

    render() {
        return (
        <>
            <Button type="primary" onClick={this.showModal}>
                提交
            </Button>
            <Modal
                title="Modal"
                visible={this.state.visible}
                onOk={this.hideModal}
                onCancel={this.hideModal}
                okText="确认"
                cancelText="取消"
            >
                <Input className="inputs" placeholder="email" onChange={this.inputChange} value={this.state.email}/>
                <Input className="inputs" placeholder="nickname" onChange={this.inputChange} value={this.state.nickname}/>       
            </Modal>
        </>
        );
    }
}
export default LocalizedModal;
