import Link from 'next/link';

const ArticleList = (props) => {
    const { article_list } = props;
    return (
        <div>
            <ul>
                {
                    article_list?.map(item => {
                        return (
                            <Link key={item.id} as={`/infos/${item.id}`} href="/infos/[item.id]" >
                                <li className="articleLi">
                                    <div className="articleTitle">{item.title}</div>
                                    <div className="articleTxt">{item.summary}</div>
                                    <div className="articleInfo">
                                        <span className="spanMargin">ELEVEN</span>
                                        <span className="spanMargin">点赞 1</span>
                                        <span className="spanMargin">阅读 2</span>
                                        <span className="spanMargin">
                                            <i>
                                                <svg t="1609076656536" class="icon" viewBox="0 0 1024 1024" version="1.1" xmlns="http://www.w3.org/2000/svg" p-id="7997" width="200" height="200"><path d="M337.36192 914.688c-3.5584 0-7.13728-0.75776-10.496-2.304-8.89856-4.096-14.61248-12.99968-14.61248-22.8096L312.25344 782.5408c0-13.29152-10.81344-24.09984-24.12544-24.09984l-122.88 0c-41.00096 0-74.35264-33.3568-74.35264-74.33216L90.89536 235.6992c0-40.9856 33.35168-74.33728 74.35264-74.33728l708.80768 0c40.97536 0 74.32704 33.35168 74.32704 74.33728l0 448.41472c0 40.97536-33.35168 74.33216-74.32704 74.33216l-336.19968 0c-5.76 0-11.35104 2.048-15.73888 5.8368l-168.41728 144.3584C349.06112 912.62464 343.24992 914.688 337.36192 914.688z" p-id="7998" fill="#b4b4b4"></path></svg>
                                            </i>
                                             3</span>
                                    </div>
                                </li>
                            </Link>
                        )
                    })
                }
            </ul>
            <style jsx>
                {`
                    .articleTitle{
                        color: #333333;
                        font-size: 18px;
                        font-weight: 700;
                        line-height: 30px;
                        cursor: pointer;
                    }
                    .articleTxt{
                        font-size: 13px;
                        color: #999999;
                        line-height: 30px;
                        cursor: pointer;
                        margin-bottom: 10px;
                    }
                    .articleLi{
                        padding: 15px 0 20px 0;
                        border-bottom: 1px solid #f0f0f0;
                        margin: 15px 0;
                    }
                    .articleInfo{
                        padding-right: 0!important;
                        font-size: 12px;
                        font-weight: 400;
                        line-height: 20px;
                        color: #b4b4b4;
                    }
                    .spanMargin{
                        margin-right: 10px;
                    }
                `}
            </style>
        </div>
    )
}

export default ArticleList;
