import { connect } from 'react-redux'
import LeavingMessage from '../components/LeavingMessage';
import VisitorList from '../components/VisitorList';
import ArticleList from '../components/ArticleList';
import Head from 'next/head';
import initializeStore from '../store/store';
import http from '../http';
import useInterval from '../lib/useInterval';

const Index = ({ comLabelInteger, initialReduxState }) => {
    const { index_article_list, index_message_list } = initialReduxState;
    console.log(index_message_list, 'index_message_list');
    comLabelInteger(0) // 首页
    return (
        <div className="homeWrap">
            <Head>
                <title>首页</title>
            </Head>
            <div className="homeLeft">
                <ArticleList article_list={index_article_list.data.list}/>
            </div>
            <div className="homeRight">
                <LeavingMessage />
                <VisitorList />
            </div>
            <style jsx>
            {`
                .homeWrap{
                    display: flex;
                    max-width: 1200px;
                    width: 1200px;
                    padding: 20px 0;
                    margin:0 auto;
                }
                .homeLeft{
                    width: 800px;
                    padding: 20px;
                }
            `}
            </style>
        </div>
    )
};

function mapStateToProps(state) {
    const { com_label_integer } = state
    return {
        com_label_integer
    }
}

function mapDispatchToProps(dispatch) {
    return {
        comLabelInteger(com_label_integer = 0) {
            dispatch({ type: 'comLabelInteger', com_label_integer })
        }
    }
}

export async function getServerSideProps () {
    const reduxStore = initializeStore()
    const { dispatch } = reduxStore;
    const data = await http({ url: '/article/getArticleList', method: 'post' , params: {
        pageSize: 100,
        pageNum: 1
    }})

    dispatch({
        type: 'indexArticleList',
        data
    })

    http({url: '/message/getMessageList', method: 'get' , params: {}}).then(data => {
        console.log(data, '=====>>>>>');
        dispatch({
            type: 'indexMessageList',
            data
        })
    })

    return { 
        props: { 
            initialReduxState: reduxStore.getState() 
        }
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Index)
