import { connect } from 'react-redux'
import { withRouter } from 'next/router';
import LeavingMessage from '../components/LeavingMessage';
import VisitorList from '../components/VisitorList';
import ArticleList from '../components/ArticleList';
import Head from 'next/head'

const Index = withRouter(({ handleLabelPage, articleList }) => {
    handleLabelPage(1)
    return (
        <div className="homeWrap">
            <Head>
                <title>首页</title>
            </Head>
            <div className="homeLeft">
                <ArticleList articleList={articleList}/>
            </div>
            <div className="homeRight">
                <LeavingMessage />
                <VisitorList />
            </div>
            <style jsx>
            {`
                .homeWrap{
                    display: flex;
                    max-width: 1200px;
                    width: 1200px;
                    padding: 20px 0;
                    margin:0 auto;
                }
                .homeLeft{
                    width: 800px;
                    padding: 20px;
                }
            `}
            </style>
        </div>
    )
});

function mapStateToProps(state) {
    const { labelPage } = state
    return {
        labelPage
    }
}

function mapDispatchToProps(dispatch) {
    return {
        handleLabelPage(labelPage) {
            dispatch({ type: 'handleLabelPage', labelPage })
        }
    }
}


Index.getInitialProps = async () => {
    const data = await http({ url: '/article/getArticleList', method: 'post' , params: {
        pageSize: 100,
        pageNum: 1
    }})
    console.log(data, '=====');
    return {
        articleList: data.data
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Index)

