import { withRouter } from 'next/router';
import { connect } from 'react-redux'
import LeavingMessage from '../components/LeavingMessage';
import VisitorList from '../components/VisitorList';
import ArticleList from '../components/ArticleList';
import initializeStore from '../store/store';
import Head from 'next/head'
import http from '../http';

const Post = withRouter(({ initialReduxState, comLabelInteger, router}) => {
    const { index_article_list } = initialReduxState;
    comLabelInteger(router.query.id)
    return (
    <div className="homeWrap">
        <Head>
            <title>{router.query.name}</title>
        </Head>
            <div className="homeLeft">
                <ArticleList index_article_list={index_article_list.data.list}/>
            </div>
            <div className="homeRight">
                <LeavingMessage />
                <VisitorList />
            </div>
        <style jsx>
        {`
            .homeWrap{
                display: flex;
                max-width: 1200px;
                width: 1200px;
                padding: 20px 0;
                margin:0 auto;
            }
            .homeLeft{
                width: 800px;
                padding: 20px;
            }
        `}
        </style>
    </div>
    )
});

function mapStateToProps(state) {
    const { com_label_integer } = state
    return {
        com_label_integer
    }
}

function mapDispatchToProps(dispatch) {
    return {
        comLabelInteger(com_label_integer = 0) {
            dispatch({ type: 'comLabelInteger', com_label_integer })
        }
    }
}

export async function getServerSideProps () {
    const reduxStore = initializeStore()
    const { dispatch } = reduxStore;
    const data = await http({ url: '/article/getArticleList', method: 'post' , params: {
        pageSize: 100,
        pageNum: 1
    }})
    dispatch({
        type: 'postArticleList',
        data
    })
    return { 
        props: { 
            initialReduxState: reduxStore.getState() 
        }
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Post)

