import Link from 'next/link';

const ArticleList = (props) => {
    const { article_list } = props;
    return (
        <div>
            <ul>
                {
                    article_list?.map(item => {
                        return (
                            <Link key={item.id} as={`/infos/${item.id}`} href="/infos/[item.id]" >
                                <li className="articleLi">
                                    <div className="articleTitle">{item.title}</div>
                                    <div className="articleTxt">{item.summary}</div>
                                    <div className="articleInfo">
                                        <span className="spanMargin">ELEVEN</span>
                                        <span className="spanMargin">点赞</span>
                                        <span className="spanMargin">阅读</span>
                                        <span className="spanMargin">留言</span>
                                    </div>
                                </li>
                            </Link>
                        )
                    })
                }
            </ul>
            <style jsx>
                {`
                    .articleTitle{
                        color: #333333;
                        font-size: 18px;
                        font-weight: 700;
                        line-height: 30px;
                        cursor: pointer;
                    }
                    .articleTxt{
                        font-size: 13px;
                        color: #999999;
                        line-height: 30px;
                        cursor: pointer;
                        margin-bottom: 10px;
                    }
                    .articleLi{
                        padding: 15px 0 20px 0;
                        border-bottom: 1px solid #f0f0f0;
                        margin: 15px 0;
                    }
                    .articleInfo{
                        padding-right: 0!important;
                        font-size: 12px;
                        font-weight: 400;
                        line-height: 20px;
                        color: #b4b4b4;
                    }
                    .spanMargin{
                        margin-right: 10px;
                    }
                `}
            </style>
        </div>
    )
}

export default ArticleList;
