import { Modal, Button, Input } from "antd";
import { ExclamationCircleOutlined } from "@ant-design/icons";
import React from 'react';

class LocalizedModal extends React.Component {
    constructor(props) {
        super(props)
        console.log(this.props.visible, '====');
        this.state = {
            visible: this.props.visible,
            user_ip: this.props.user_ip
        }
    }
    showModal = () => {
        this.setState({
            visible: true,
        });

    };

    hideModal = () => {
        this.setState({
            visible: false,
        });

        const params = {
            ip: this.state.user_ip,
            email: '',
            nickname: ''
        };
        // ip,
        // email,
        // nickname,
        // agent

        // httpAgent({url: '/user/addUser', method: 'post' , params}).then(res => {
        //     // this.setState({
        //     //     post_view_list: res
        //     // })
        // })
    };
    componentWillReceiveProps(nextProps) {
        const { data } = this.state
        console.log(nextProps, 'nextProps');
        const newdata = nextProps.data.toString()
        if (data.toString() !== newdata) {
            this.setState({
                data: nextProps.data,
            })
        }
    }
    render() {
        console.log(this.state, 'sss');
        return (
        <>
            {/* <Button type="primary" onClick={this.showModal}>
                提交
            </Button> */}
            <Modal
                title="Modal"
                visible={this.state.visible}
                onOk={this.hideModal}
                onCancel={this.hideModal}
                okText="确认"
                cancelText="取消"
            >
                <Input className="inputs" placeholder="email" onChange={this.inputChange} value={this.state.email}/>
                <Input className="inputs" placeholder="nickname" onChange={this.inputChange} value={this.state.nickname}/>       
            </Modal>
        </>
        );
    }
}
export default LocalizedModal;
