import { useRouter } from "next/router";
import ErrorPage from "next/error";
import VisitorList from '../../components/VisitorList';
import LeavingMessage from '../../components/LeavingMessage';
import PostBody from "../../components/postBody";
import httpServer from '../../httpServer';
import Layout from '../../components/Layout';
import Header from '../../components/Header';

export default function Info({ slug, initialReduxState, morePosts, preview }) {
    const router = useRouter();
    const { info_Article_Vo, info_message_list, info_view_list, com_label_integer, com_sort_list, html_head_info, au_in_for } = initialReduxState;
    if (!router.isFallback && !slug) {
        return <ErrorPage statusCode={404} />;
    }
    return (
        <Layout html_head_info={html_head_info}>
            <Header com_sort_list={com_sort_list.data} com_label_integer={com_label_integer}/>
            <div className="bodyWrap">
                <div className="homeWrap">
                    <div>
                        <PostBody content={info_Article_Vo.data.html} au_in_for={au_in_for}/>
                    </div>
                    <div className="homeRight">
                        <VisitorList view_list={info_view_list?.data}/>
                        <LeavingMessage message_list={info_message_list?.data}/>
                    </div>
                </div>
                <div className="_3Pnjry">
                    <div className="svgZan ">
                        <i className="anticon">
                            <svg t="1609041359031" class="icon" viewBox="0 0 1024 1024" version="1.1" p-id="2584" width="24" height="24">
                            <path d="M728.064 343.943529c-17.648941-2.891294-23.552-20.239059-26.503529-28.912941V104.026353C701.560471 46.200471 654.396235 0 595.425882 0c-53.007059 0-97.28 40.478118-106.134588 89.569882-29.997176 184.862118-138.541176 255.457882-217.630118 280.937412a26.142118 26.142118 0 0 0-18.130823 24.877177v560.067764c0 19.817412 16.022588 35.84 35.84 35.84h535.973647c56.018824-11.565176 94.328471-31.804235 120.892235-86.738823l120.832-416.105412c23.552-75.173647-14.757647-147.395765-100.231529-144.564706h-238.772706z m-571.813647 31.744H76.619294C35.358118 375.687529 0 410.383059 0 450.861176v462.426353c0 43.369412 32.406588 78.004706 76.619294 78.004706h79.631059c27.708235 0 50.115765-22.407529 50.115765-50.115764V425.863529a50.115765 50.115765 0 0 0-50.115765-50.115764z"></path>
                            </svg>
                        </i>
                    </div>
                    <div className="P63n6G">
                        <div className="_2LKTFF">
                            <span className="_1GPnWJ" role="button" tabindex="-1" aria-label="查看点赞列表">1赞</span>
                            <span className="_1GPnWJ">2赞</span>
                        </div>
                    </div>
                </div>
                <style jsx>
                {`
                    .homeRight{
                        width: 260px;
                    }
                    ._3Pnjry {
                        position: fixed;
                        z-index: 98;
                        top: 216px;
                        -webkit-user-select: none;
                        -moz-user-select: none;
                        -ms-user-select: none;
                        user-select: none;
                        left: calc((100vw - 1000px)/2 - 78px);
                    }
                    .svgZan{
                        justify-content: center;
                        width: 48px;
                        height: 48px;
                        font-size: 18px;
                        border-radius: 50%;
                        box-shadow: 0 2px 10px rgba(0,0,0,.05);
                        background-color: #fff;
                        display: flex;
                        align-items: center;
                    }
                    .anticon {
                        cursor: pointer;
                        display: inline-block;
                        color: inherit;
                        font-style: normal;
                        line-height: 0;
                        text-align: center;
                        text-transform: none;
