import Layout from '../components/Layout';
import React from 'React';

class Post extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            post_view_list: null
        };
    }
    async componentDidMount() {
        console.log(1);
        httpAgent({url: '/user/getUserView', method: 'post' , params: {
            id: 1
        }}).then(res => {
            this.setState({
                post_view_list: res
            })
        })
    }
    render() {
        const { post_article_list, post_message_list, com_label_integer, html_head_info } = this.props.initialReduxState;
