import React from 'React';
import { Input } from 'antd';
import { isEmpty } from 'zgl-utils-js';
import httpAgent from '../httpAgent';
import cn from 'classnames';

const { TextArea } = Input;
import LocalizedModal from '../components/others/LocalizedModal';
class Comment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isUser: false,
            visible: false,
            user_ip: this.props.user_ip,
            content: '',
            com_user_info: {},
            commentList: [],
            length: 0,
            commlist: [],
            comChildList: [],
            oneIndex: null
        };
        this.articleId = this.props.slug;
        this.commentLevel = 1;
        this.parentCommentId = null; // 父评论id
        this.parentCommentUserId = null; // 父级评论的userid
        this.replyCommentId = null; // 被回复的userid
        this.replyCommentUserId = null; // 被回复的用户id
    }
    handleAddCommentUser = () => {
        if (!this.state.isUser) {
            this.setState({
                visible: true
            })
            return;
        }
        const params = {
            articleId: this.articleId, // 文章id
            userid: this.com_user_info.id, // 评论人id
            parentCommentId: this.parentCommentId, // 父评论id
            parentCommentUserId: this.parentCommentUserId, // 父级评论的userid
            replyCommentId: this.replyCommentId, // 被回复的userid
            replyCommentUserId: this.replyCommentUserId, // 被回复的用户id
            commentLevel: this.commentLevel, // 评论级别
            content: this.state.content // 内容
        }
        httpAgent({url: '/message/addCommentList', method: 'post' , params}).then(res => {
            this.setState({
                visible: false
            })
        })
    }
    textareafn = ({ target: { value } }) => {
        this.setState({
            content: value
        })
    }
    onInputTextBlur = () => {
        if (this.commentLevel !== 2) {
            this.commentLevel = 1;
        }
        this.onReplyMssage(null, null, null, null, this.commentLevel)
    }
    onReplyMssage = (parentCommentId, parentCommentUserId, replyCommentId, replyCommentUserId, commentLevel) => {
        this.commentLevel = commentLevel;
        this.parentCommentId = parentCommentId;
        this.parentCommentUserId = parentCommentUserId;
        this.replyCommentId = replyCommentId;
        this.replyCommentUserId = replyCommentUserId;
    }
    onReviewBackMessage = (one, cur, index) => {
        if (one === 1) {
            const commlist = this.state.commlist;
            commlist.push(cur)
            this.setState({
                commlist
            })
        }
        if (one === 2) {
            const comChildList = this.state.comChildList;
            comChildList.push(cur + '_' + index);
            this.setState({
                comChildList
            })
        }
    }
    componentDidMount() {
        httpAgent({url: '/message/getCommentList', method: 'post' , params: {
            articleId: this.props.slug
        }}).then(res => {
            let length = res.data.length;
            res.data.forEach(element => {
                length = length + element.child.length
            });
            this.setState({
                commentList: res.data,
                length
            })
        })
    }
    componentWillReceiveProps(nextProps) {
        const { com_user_info } = this.state;
        if (JSON.stringify(nextProps.com_user_info) != JSON.stringify(com_user_info)) {
            this.setState({
                com_user_info: nextProps.com_user_info
            })
            if (!isEmpty(nextProps.com_user_info)) {
                this.setState({
                    isUser: true
                })
            }
        }
    }
    render() {
        const { com_user_info, commentList, length, commlist, comChildList } = this.state;
        return (
            <div id="note-page-comment">
                <LocalizedModal visible={this.state.visible} user_ip={this.state.user_ip}/>
                <section className="ouvJEz">
                    <div className="_26JdYM">
                        <img className="_3LHFA-" src="https://upload.jianshu.io/users/upload_avatars/25124160/9f030a41-c698-4d43-a999-ff9a8cc3c6e6?imageMogr2/auto-orient/strip|imageView2/1/w/80/h/80/format/webp" alt="" />
                        <div className="_3GKFE3">
                            <TextArea className="_1u_H4i" onFocus={this.onInputTextBlur}  onChange={this.textareafn} value={this.state.content} placeholder="写下你的评论..."></TextArea>
                            <div>
                                <div className="_3IXP9Q">
                                    <div className="userInfos">
                                        {com_user_info?.nickname?com_user_info?.nickname: ''}
                                    </div>
                                    <div className="_3Tp4of">
                                        <button onClick={() => this.handleAddCommentUser} type="button" className="_1OyPqC">
                                            <span>发布</span>
                                        </button>
                                        <button type="button" className="_1OyPqC">
                                            <span>取消</span>
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <h3 className="QxT4hD">
                        <div className="_10KzV0">
                            <span>全部评论</span>
                            <span className="_2R7vBo">{length}</span>
                        </div>
                    </h3>
                    {
                        commentList.map((comment, index) => {
                            return (
                                <div key={comment.id} className="_2IUqvs _3uuww8" id="comment-63103346">
                                    <a className="_1OhGeD" href="#" target="_blank" rel="noopener noreferrer">
                                        <img className="_1_jhXc" src={comment.userInfo.headPicture} alt="" />
                                    </a>
                                    <div className="_1K9gkf">
                                        <div className="_23G05g">
                                            <a className="_1OhGeD" href="#" target="_blank" rel="noopener noreferrer">{comment.userInfo.nickname}</a>
                                        </div>
                                        <div className="_1xqkrI">
                                            <span>{index + 1}楼 </span><time >{comment.createDate}</time>
                                        </div>
                                        <div className="_2bDGm4">{comment.content}</div>
                                        <div className="_2ti5br">
                                            <div className="_3MyyYc">
                                                <span className="_1Jvkh4" onClick={() => this.onReviewBackMessage(1, index)} role="button" aria-label="添加评论">回复</span>
                                            </div>
                                        </div>
                                        <div className={cn('hideBlock', {
                                            'showBlock': commlist.some(so => so === index)
                                        })}>
                                            <div className="_3GKFE3" >
                                                <TextArea className="_1u_H4i" placeholder="写下你的评论..."></TextArea>
                                                <div>
                                                    <div className="_3IXP9Q">
                                                        <div className="SKZUyR"></div>
                                                        <div className="_3Tp4of">
                                                            <button disabled="" onClick={() => this.onReplyMssage(comment.id, comment.userid, null, null, 2)} type="button" className="_1OyPqC _3Mi9q9 _1YbC5u"><span>发布</span></button>
                                                            <button type="button" className="_1OyPqC _2nzlC_"><span>取消</span></button>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                        {
                                            comment.child.map((child, cur) => {
                                                return (
                                                    <div key={child.id} className="_2kvBge">
                                                        <div className="_3g0yKR">
                                                            <div className="_3d_vFY">
                                                                <div className="margin-left: 10px;">
                                                                    <div className="_23G05g">
                                                                        <a className="_1OhGeD" href="#" target="_blank" rel="noopener noreferrer">{child.userInfo.nickname}</a>
                                                                        {
                                                                            child.isAuthor === 1? <span className="_3pyYXB">作者</span>: <span></span>
                                                                        }
                                                                    </div>
                                                                    <div className="_1xqkrI">
                                                                        <time className="2020-12-18T06:45:56.000Z">{child.createDate}</time>
                                                                    </div>
                                                                </div>
                                                            </div>
                                                            <div className="_2bDGm4">
                                                                {
                                                                    child.replyCommentNickName?
                                                                    <a href="#" className="maleskine-author" target="_blank" data-user-slug="a92d6c1e2f2f">@{child.replyCommentNickName}</a> 
                                                                    : <span></span>
                                                                }
                                                                {child.content}
                                                            </div>
                                                            <div className="_2ti5br">
                                                                <div className="_3MyyYc">
                                                                    <span className="_1Jvkh4" role="button" aria-label="回复评论" onClick={() => this.onReviewBackMessage(2, cur, index)} >回复</span>
                                                                </div>
                                                            </div>
                                                            <div className={cn('hideBlock', {
                                                                    'showBlock': comChildList.some(so => so === cur + '_' + index)
                                                                })}>
                                                                <div className="_3GKFE3" >
                                                                    <TextArea className="_1u_H4i" placeholder="写下你的评论..."></TextArea>
                                                                    <div>
                                                                        <div className="_3IXP9Q">
                                                                            <div className="SKZUyR"></div>
                                                                            <div className="_3Tp4of">
                                                                                <button disabled=""  onClick={() => this.onReplyMssage(comment.id, comment.userid, child.id, child.userid, 2)} type="button" className="_1OyPqC _3Mi9q9 _1YbC5u"><span>发布</span></button>
                                                                                <button type="button" className="_1OyPqC _2nzlC_"><span>取消</span></button>
                                                                            </div>
                                                                        </div>
                                                                    </div>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                )
                                            })
                                        }
                                    </div>
                                </div>
                            )
                        })
                    }
            </section>
                <style jsx>
                    {
                        `
                        .showBlock{
                            display: block;
                        }
                        .hideBlock{
                            display: none;
                        }
                        .maleskine-author{
                            margin-right: 5px;
                        }
                        .userInfos{
                            display: flex;
                        }
                        .inputs{
                            font-size: 13px;
                            border: 1px solid #eee;
                            border-radius: 4px;
                            background-color: #fafafa;
                        }
