    //         res.data && res.data.forEach(element => {
    //             length = length + element.child.length
    //         });
    //         this.setState({
    //             length,
    //             commentList: res.data
    //         })
    //     })
    // }
    render() {
        const { au_in_for, content, user_ip, com_user_info, slug, commentList, length } = this.state;
        const { title } = au_in_for;
        return (
            <div>
                <div className="md-editor-container">
                    <h1>{title}</h1>
                    <Avatar au_in_for={au_in_for} length={length}/>
                    <div
                        className="preview-container"
                        dangerouslySetInnerHTML={{ __html: content }}
                    />
                </div>
                {/* <Comment commentList={commentList} length={length} slug={slug} user_ip={user_ip} com_user_info={com_user_info}/> */}
