import Layout from '../components/Layout';
import Link from 'next/link';
import http from '../http';

const Index = (props) => (
    <Layout>
        <Head>
            <title>首页</title>
        </Head>
        <ul>
            {props.shows.map(show => {
                return (
                    <li key={show.value}>
                        <Link as={`/load/${show.value}`} href={`/post?id=${show.value}`}>
                            <a>{show.name}</a>
                        </Link>
                    </li>
                );
            })}
        </ul>
        <style jsx>
        {`
            *{
                margin:0;
                padding:0;
            }
            ul{
                margin-top:20px;
                padding:20px;
                background-color:#000;
            }
            li{
                list-style:none;
                margin:5px 0;
            }
            a{
                text-decoration:none;
                color:#B4B5B4;
                font-size:24px;
            }
            a:hover{
                opacity:0.6;
            }
        `}
        </style>
    </Layout>
);

Index.getInitialProps = async () => {
    const data = await http({ url: '/sort/getSortList', method: 'get' , params: {}})
    return {
        shows: data.data
    }
}

export default Index;
