import { connect } from 'react-redux'
import Link from 'next/link';
import Router from 'next/router'
import imgs from '../images/images';
import styles from '../styles/header.module.scss';

const Header = ({ labelPage, sortList}) => {
    // console.log(labelPage, 'labelPage');
    // const goPage = (show) => {
    //     Router.push({
    //         pathname: '/post',
    //         query: {
    //             id: show.value,
    //             name: show.name
    //         }}, `/load/${show.value}`)
    // }
    return (
        <header className={styles.header}>
            <div>
                <img className={styles.headLogoImg} src={ imgs.headerLogo } />
            </div>
            <div className={styles.sortList}>
                    <Link href="/">
                        <div className={styles.span, styles.spanShow}>首页</div>
                    </Link>
                    {sortList.map(show => {
                        return (
                            <Link key={show.value} as={`/load/${show.value}`} href={`/post?id=${show.value}&name=${show.name}`}>
                                <div className={styles.span}>{show.name}</div>
                            </Link>
                        );
                    })}
            </div>
        </header>
    )
}

function mapStateToProps(state) {
    const { labelPage } = state
    return {
        labelPage
    }
}

function mapDispatchToProps(dispatch) {
    return {
        handleLabelPage() {
            dispatch({ type: 'handleLabelPage' })
        }
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Header)
// export default Header;
