import { useRouter } from "next/router";
import ErrorPage from "next/error";
import VisitorList from '../../components/VisitorList';
import LeavingMessage from '../../components/LeavingMessage';
import PostBody from "../../components/postBody";
import httpServer from '../../httpServer';
import Layout from '../../components/Layout';
import Header from '../../components/Header';

export default function Info({ slug, initialReduxState, morePosts, preview }) {
    const router = useRouter();
    const { info_Article_Vo, info_message_list, info_view_list, com_label_integer, com_sort_list, html_head_info, au_in_for } = initialReduxState;
    if (!router.isFallback && !slug) {
        return <ErrorPage statusCode={404} />;
    }
    return (
        <Layout html_head_info={html_head_info}>
            <Header com_sort_list={com_sort_list.data} com_label_integer={com_label_integer}/>
            <div className="bodyWrap">
                <div className="homeWrap">
                    <div>
                        <PostBody content={info_Article_Vo.data.html} au_in_for={au_in_for}/>
                    </div>
                    <div className="homeRight">
                        <VisitorList view_list={info_view_list?.data}/>
                        <LeavingMessage message_list={info_message_list?.data}/>
                    </div>
                </div>
                <div className="_3Pnjry">
                    <div class="_1pUUKr">
                            <div class="_2VdqdF" role="button" tabindex="-1" aria-label="给文章点赞"></div>
                        <div class="P63n6G">
                            <div class="_2LKTFF">
                            <span class="_1GPnWJ" role="button" tabindex="-1" aria-label="查看点赞列表">1赞</span>
                            <span class="_1GPnWJ">2赞</span></div>
                        </div>
                    </div>
                    <div class="_1pUUKr">
                        <div class="_2VdqdF" role="button" tabindex="-1" aria-label="赞赏作者"></div>
                        <div class="P63n6G" role="button" tabindex="-1" aria-label="查看赞赏列表">赞赏</div>
                    </div>
                </div>
                <style jsx>
                {`
                    .homeRight{
                        width: 260px;
                    }
                    ._3Pnjry {
                        position: fixed;
                        z-index: 98;
                        top: 216px;
                        -webkit-user-select: none;
                        -moz-user-select: none;
                        -ms-user-select: none;
                        user-select: none;
                        left: calc((100vw - 1000px)/2 - 78px);
                    }
                `}
                </style>
            </div>
        </Layout>
    );
};

export async function getServerSideProps({ params }) {
    const initialReduxState = await httpServer({url: '/api/info', method: 'post' , params: {
        id: params.slug
    }})
    return {
        props: {
            slug: params.slug,
            initialReduxState: initialReduxState.data
        }
    }
}
