import { Modal, Button, Form, Input } from "antd";
import React from 'react';
import httpAgent from '../../httpAgent';
import { allAgentInfo } from 'zgl-utils-js';

class LocalizedModal extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            visible: this.props.visible,
            user_ip: this.props.user_ip
        }
    }
    componentWillReceiveProps(nextProps) {
        const { visible } = this.state;
        const newdata = nextProps.visible.toString();
        if (visible.toString() !== newdata) {
            this.setState({
                visible: nextProps.visible,
                content: nextProps.content
            })
        }
    }
    layout = {
        labelCol: { span: 8 },
        wrapperCol: { span: 16 },
    };

    validateMessages = {
        required: '${label} is required!',
        types: {
          email: '${label} is not a valid email!',
          number: '${label} is not a valid number!',
        },
        number: {
          range: '${label} must be between ${min} and ${max}',
        },
    };

    onFinish = values => {

        this.setState({
            visible: false,
        });

        const params = {
            ip: this.state.user_ip,
            ...values,
            agent: allAgentInfo()
        };
        httpAgent({url: '/user/addUser', method: 'post' , params}).then(res => {
            this.setState({
                visible: false
            })
        })
    };
    showModal = () => {
        this.setState({visible: true})
    };
    handleOk = () => {
        setTimeout(() => {
            this.setState({visible: false})
        }, 2000);
    };
    handleCancel = () => {
        this.setState({visible: false})
    };
    render() {
        return (
        <>
            <Modal
                title="完善一次信息"
                visible={this.state.visible}
                onOk={this.handleOk}
                onCancel={this.handleCancel}
                footer={false}
                okText="确认"
                cancelText="取消"
            >
                <Form {...this.layout} name="nest-messages" onFinish={this.onFinish} validateMessages={this.validateMessages}>
                    <Form.Item 
                        name="nickname" 
                        label="昵称" rules={[{ required: true }]}>
                        <Input />
                    </Form.Item>
                    <Form.Item 
                        name="email" 
                        label="邮箱" rules={[{ type: 'email' }]}>
                        <Input />
                    </Form.Item>
                    <Form.Item wrapperCol={{ ...this.layout.wrapperCol, offset: 8 }}>
                        <Button type="primary" htmlType="submit">
                            提交
                        </Button>
                    </Form.Item>
                </Form>      
            </Modal>
        </>
        );
    }
}
export default LocalizedModal;
