import { useRouter } from "next/router";
import ErrorPage from "next/error";
import VisitorList from '../../components/VisitorList';
import LeavingMessage from '../../components/LeavingMessage';
import PostBody from "../../components/postBody";
import httpServer from '../../httpServer';
import Layout from '../../components/Layout';
import Header from '../../components/Header';
import InfoThumdsIcon from '../../components/others/InfoThumdsIcon';
import { connect } from 'react-redux';
import React from 'React';

// const Info = ({ slug, initialReduxState, com_user_info, comUserInfo }) => {
class Info extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            initialReduxState: this.props.initialReduxState,
            slug: this.props.slug,
            com_user_info: this.props.com_user_info,
            comUserInfo: this.props.comUserInfo
        }
    }
    render() {
        const router = useRouter();
        if (!router.isFallback && !slug) {
            return <ErrorPage statusCode={404} />;
        }
        const { info_Article_Vo, info_message_list, info_view_list, com_label_integer, com_sort_list, html_head_info, au_in_for, user_ip } = this.state.initialReduxState;
        return (
            <Layout html_head_info={html_head_info}>
                <Header com_sort_list={com_sort_list.data} com_label_integer={com_label_integer}/>
                <div className="bodyWrap">
                    <div className="homeWrap">
                        <div>
                            <PostBody content={info_Article_Vo.data.html} au_in_for={au_in_for} user_ip={user_ip}/>
                        </div>
                        <div className="homeRight">
                            <VisitorList view_list={info_view_list?.data}/>
                            <LeavingMessage message_list={info_message_list?.data}/>
                        </div>
                    </div>

                    <InfoThumdsIcon 
                        thumdsStatus={info_Article_Vo.data.thumdsStatus} 
                        thumdsSum={info_Article_Vo.data.thumdsSum}
                        articleId={info_Article_Vo.data.id}
                        ip={user_ip}
                    />
                    <style jsx>
                    {`
                        .homeRight{
                            width: 260px;
                        }
                    `}
                    </style>
                </div>
            </Layout>
        );
    }
};

export async function getServerSideProps({ params }) {
    const initialReduxState = await httpServer({url: '/api/info', method: 'post' , params: {
        id: params.slug
    }})
    return {
        props: {
            slug: params.slug,
            initialReduxState: initialReduxState.data
        }
    }
}

function mapStateToProps(state) {
    const { com_user_info } = state
    return {
        com_user_info
    }
}

function mapDispatchToProps(dispatch) {
    return {
        comUserInfo() {
            dispatch({ type: 'comUserInfo' })
        }
    }
  }

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Info)
