import styles from '../../styles/articleTrees.module.scss';
import { Tree } from 'antd';

const { DirectoryTree } = Tree;

const treeData = [
    {
        title: 'parent 0',
        key: '0-0',
        children: [
            { title: 'leaf 0-0', key: '0-0-0', isLeaf: true },
            { title: 'leaf 0-1', key: '0-0-1', isLeaf: true },
        ],
    },
    {
        title: 'parent 1',
        key: '0-1',
        children: [
            { title: 'leaf 1-0', key: '0-1-0', isLeaf: true },
            { title: 'leaf 1-1', key: '0-1-1', isLeaf: true },
        ],
    },
];
export default function ArticleTrees({com_class_list}) {
    const com_classList = com_class_list.map(item => {
        return getItemChild(item)
    })
    function getItemChild(item) {
        // console.log(item, '=');
        if (item?.isLeaf) {
            return {
                title: item.name,
                key: item.value
            }
        } else {
            return {
                title: item?.name,
                key: item?.value,
                children: getItemChild(item?.children)
            }
        }
    }
    const onSelect = (keys, event) => {
        console.log('Trigger Select', keys, event);
    };

    const onExpand = () => {
        console.log('Trigger Expand');
    };
    return (
        <div className={styles.trees}>
           <DirectoryTree
                multiple
                defaultExpandAll
                onSelect={onSelect}
                onExpand={onExpand}
                treeData={com_classList}
            />
        </div>
    )
}
