import styles from '../../styles/articleTrees.module.scss';
import { Tree } from 'antd';
import { isEmpty } from 'zgl-utils-js';
import React from 'react';


const { DirectoryTree } = Tree;

export default class ArticleTrees extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            com_class_list: this.props.com_class_list
        }
    }
    getItemChild = (item) => {
        return item.map(child => {
            if (child.isLeaf) {
                return {
                    title: child.name,
                    key: child.value
                }
            } else {
                return {
                    title: child.name,
                    key: child.value,
                    children: getItemChild(child.children)
                }
            }
        })
    }
    com_classList = !isEmpty(com_class_list) && com_class_list.map(item => {
        if (item.isLeaf) {
            return {
                title: item.name,
                key: item.value
            }
        } else {
            return {
                title: item.name,
                key: item.value,
                children: getItemChild(item.children)
            }
        }
    })
    updateTreeData = (list, key, children) => {
        return list.map((node) => {
          if (node.key === key) {
            return { ...node, children };
          } else if (node.children) {
            return { ...node, children: updateTreeData(node.children, key, children) };
          }

          return node;
        });
    }

    onSelect = (keys, event) => {
        console.log('Trigger Select', keys, event);
    };

    onExpand = () => {
        console.log('Trigger Expand');
    };
    render() {
        return (
            <div className={styles.trees}>
                <div className={styles.visitorTitle}> 
                    分类列表 
                </div>
               <DirectoryTree
                    multiple
                    onSelect={onSelect}
                    onExpand={onExpand}
                    treeData={com_classList}
                />
            </div>
        )
    }
}
