import { connect } from 'react-redux'
import { withRouter } from 'next/router';
import LeavingMessage from '../components/LeavingMessage';
import VisitorList from '../components/VisitorList';
import ArticleList from '../components/ArticleList';
import Head from 'next/head'

const Index = withRouter(({ handleLabelPage, getArticleList }) => {
    handleLabelPage(1)
    getArticleList({
        pageSize: 100,
        pageNum: 1
    });
    return (
        <div className="homeWrap">
            <Head>
                <title>首页</title>
            </Head>
            <div className="homeLeft">
                <ArticleList/>
            </div>
            <div className="homeRight">
                <LeavingMessage />
                <VisitorList />
            </div>
            <style jsx>
            {`
                .homeWrap{
                    display: flex;
                    max-width: 1200px;
                    width: 1200px;
                    padding: 20px 0;
                    margin:0 auto;
                }
                .homeLeft{
                    width: 800px;
                    padding: 20px;
                }
            `}
            </style>
        </div>
    )
});

function mapStateToProps(state) {
    console.log(state, '=====state');
    const { labelPage, articleList } = state
    console.log(articleList, 'articleList');
    return {
        labelPage,
        articleList
    }
}

function mapDispatchToProps(dispatch) {
    return {
        handleLabelPage(labelPage) {
            dispatch({ type: 'handleLabelPage', labelPage })
        },
        getArticleList(params) {
            dispatch({ type: 'getArticleList', params })
        }
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Index)
