import LeavingMessage from '../components/LeavingMessage';
import Link from 'next/link';
import http from '../http';
import Head from 'next/head'

const Index = (props) => (
    <div className="homeWrap">
        <Head>
            <title>首页</title>
        </Head>
            <div className="homeLeft">
                <ul>
                    <li>
                        <div className="articleTitle">标题标题标题标题标题标题标题</div>
                        <div className="articleTxt">描述描述描述描述</div>
                    </li>
                </ul>
            </div>
            <div className="homeRight">
                <LeavingMessage />
            </div>
        <style jsx>
        {`
            .homeWrap{
                display: flex;
                max-width: 1200px;
                width: 1200px;
                padding: 20px 0;
                margin:0 auto;
            }
            .homeLeft{
                width: 800px;
                padding: 20px;
            }
            .articleTitle{
                color: #333333;
                font-size: 18px;
                line-height: 30px;
                cursor: pointer;
            }
            .articleTxt{
                font-size: 14px;
                color: #999999;
                line-height: 30px;
                cursor: pointer;
            }
        `}
        </style>
    </div>
);

Index.getInitialProps = async () => {
    const data = await http({ url: '/sort/getSortList', method: 'get' , params: {}})
    return {
        sortList: data.data
    }
}

export default Index;
