import LeavingMessage from '../components/LeavingMessage';
import VisitorList from '../components/VisitorList';
import ArticleList from '../components/ArticleList';
import initializeStore from '../store/store';
import httpServer from '../httpServer';
import httpAgent from '../httpAgent';
import Layout from '../components/Layout';
import React from 'React';

class Post extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            post_view_list: {
                data: []
            }
        };
    }
    async componentDidMount() {
        console.log(1);
        httpAgent({url: '/user/getUserView', method: 'post' , params: {
            id: 1
        }}).then(res => {
            this.setState({
                post_view_list: res
            })
        })
    }
    render() {
        const { post_article_list, post_message_list, com_label_integer, html_head_info } = this.props.initialReduxState;
        const { post_view_list } = this.state;
        console.log(post_view_list, 'post_view_listpost_view_list');
        this.props.comLabelInteger(1)
        return (
            <Layout sortList={com_label_integer.data} html_head_info={html_head_info}>
                <div className="homeWrap">
                        <div className="homeLeft">
                            <ArticleList article_list={post_article_list.data.list}/>
                        </div>
                        <div className="homeRight">
                            {/* <VisitorList view_list={post_view_list?.data}/> */}
                            <LeavingMessage message_list={post_message_list?.data}/>
                        </div>
                    <style jsx>
                    {`
                        .homeWrap{
                            display: flex;
                            max-width: 960px;
                            width: 960px;
                            padding: 0 15px;
                            margin:0 auto;
                        }
                        .homeRight{
                            margin-left: 4.16667%;
                            width: 29.16667%;
                            margin-top: 30px;
                        }
                        .homeLeft{
                            width: 66.667%;
                            padding: 20px;
                        }
                    `}
                    </style>
                </div>
            </Layout>
        );
    }
}

