  title,
  coverImage,
  date,
  excerpt,
  author,
  slug,
}) {
    return (
        <section>
            <div className="">
                <CoverImage title={title} src={coverImage} slug={slug} />
            </div>
            <div className="ImgesWrapToPostPage">
                <Link as={`/load/${slug}`} href={`/post/?id=${slug}`}>
