import React from 'React';
import { Form, Input, Button, Checkbox } from 'antd';
import { isEmpty } from 'zgl-utils-js';
import httpAgent from '../httpAgent';
import cn from 'classnames';

const { TextArea } = Input;
import LocalizedModal from '../components/others/LocalizedModal';
class Comment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isUser: false,
            visible: false,
            user_ip: this.props.user_ip,
            content: '',
            com_user_info: {},
            commentList: [],
            length: 0,
            commlist: [],
            comChildList: [],
            oneIndex: null
        };
        this.articleId = this.props.slug;
    }
    handleSubmit = ({parentCommentId, parentCommentUserId, replyCommentId, replyCommentUserId, commentLevel, content}) => {
        if (!this.state.isUser) {
            this.setState({
                visible: true
            })
            return;
        }
        if (!content) {
            return;
        }
        const params = {
            articleId: this.articleId, // 文章id
            userid: this.state.com_user_info.id, // 评论人id
            parentCommentId, // 父评论id
            parentCommentUserId, // 父级评论的userid
            replyCommentId, // 被回复的userid
            replyCommentUserId, // 被回复的用户id
            commentLevel, // 评论级别
            content // 内容
        }
        httpAgent({url: '/message/addCommentList', method: 'post' , params}).then(res => {
            if (res.code === 0) {
                this.getCommentList()
                this.setState({
                    commlist: [],
                    comChildList: []
                })
            }
            this.setState({
                visible: false
            })
        })
    }
    onReviewBackMessage = (one, cur, index) => {
        if (one === 1) {
            const commlist = this.state.commlist;
            commlist.push(cur)
            this.setState({
                commlist
            })
        }
        if (one === 2) {
            const comChildList = this.state.comChildList;
            comChildList.push(cur + '_' + index);
            this.setState({
                comChildList
            })
        }
    }
    getCommentList() {
        httpAgent({url: '/message/getCommentList', method: 'post' , params: {
            articleId: this.props.slug
        }}).then(res => {
            let length = res.data.length;
            res.data && res.data.forEach(element => {
                length = length + element.child.length
            });
            this.setState({
                commentList: res.data,
                length
            })
        })
    }
    componentDidMount() {
        this.getCommentList()
    }
    componentWillReceiveProps(nextProps) {
        const { com_user_info } = this.state;
        if (JSON.stringify(nextProps.com_user_info) != JSON.stringify(com_user_info)) {
            this.setState({
                com_user_info: nextProps.com_user_info
            })
            if (!isEmpty(nextProps.com_user_info)) {
                this.setState({
                    isUser: true
                })
            }
        }
    }
    render() {
        const { com_user_info, commentList, length, commlist, comChildList } = this.state;
        console.log(com_user_info, 'com_user_info');
        return (
            <div id="note-page-comment">
                <LocalizedModal visible={this.state.visible} user_ip={this.state.user_ip}/>
                <section className="ouvJEz">
                    <div className="_26JdYM">
                        <img className="_3LHFA-" src="https://upload.jianshu.io/users/upload_avatars/25124160/9f030a41-c698-4d43-a999-ff9a8cc3c6e6?imageMogr2/auto-orient/strip|imageView2/1/w/80/h/80/format/webp" alt="" />
                        <div className="_3GKFE3">
                            <Form onFinish={this.handleSubmit}>
                                <div className="heightNone">
                                <Form.Item
                                    name="parentCommentId"
                                    initialValue={null}
                                >
                                    <Input />
                                </Form.Item>
                                <Form.Item
                                    name="parentCommentUserId"
                                    initialValue={null}
                                >
                                    <Input />
                                </Form.Item>
                                <Form.Item
                                    name="replyCommentId"
                                    initialValue={null}
                                >
                                    <Input  />
                                </Form.Item>
                                <Form.Item
                                    name="replyCommentUserId"
                                    initialValue={null}
                                >
                                    <Input />
                                </Form.Item>
                                <Form.Item
                                    name="commentLevel"
                                    initialValue={1}
                                >
                                    <Input  />
                                </Form.Item>
                                </div>
                                <Form.Item
                                    name="content"
                                >
                                    <TextArea className="_1u_H4i" placeholder="写下你的评论..."></TextArea>
                                </Form.Item>
                                <div>
                                    <div className="_3IXP9Q">
                                        <div className="SKZUyR">
                                        {com_user_info?.nickname?com_user_info?.nickname: ''}
                                        </div>
                                        <div className="_3Tp4of">
                                            <button type="submit" disabled="" className="_1OyPqC fabuss"><span>发布</span></button>
                                            <button type="button" className="_1OyPqC"><span>取消</span></button>
                                        </div>
                                    </div>
                                </div>
                            </Form>
                        </div>
                    </div>
                    <h3 className="QxT4hD">
                        <div className="_10KzV0">
                            <span>全部评论</span>
                            <span className="_2R7vBo">{length}</span>
                        </div>
                    </h3>
                    {
                        commentList.map((comment, index) => {
                            return (
