import { createStore, applyMiddleware } from "redux";
import ReduxThunk from "redux-thunk";
import indexState from '../store/index';
import postState from '../store/post';
import comState from '../store/com';

const allState = Object.assign(indexState, postState, comState);

const initialState = Object.keys(allState).map(key => {
    return {
        [key]: allState[key]()['default']
    }
})

function reducer(state = initialState, action) {
    const backState = null;
    if (action.type === 'handleLabelPage') {
        return allState['labelPage'](action.page)['data'];
    }
    Object.keys(allState).forEach(element => {
        if (action.type === allState[element](action.page)['type']) {
            backState = allState[element](action.page)['data']
        }

    });

    if (action.type === 'testHandle') {
        return {
            indexPage: {
                articleData: action.data
            }
        };
    }
    return state;
}

