    <div className="homeWrap">
        <Head>
            <title>首页</title>
        </Head>
            <div className="homeLeft">
    1
            </div>
            <div className="homeRight">
    2
            </div>
        <style jsx>
        {`
            .homeWrap{
                display: flex;
                max-width: 1200px;
                width: 1200px;
                padding: 20px;
            }
            .homeLeft{
                border: 1px solid #000000;
                width: 800px;
            }
        `}
        </style>
    </div>
);

Index.getInitialProps = async () => {
    const data = await http({ url: '/sort/getSortList', method: 'get' , params: {}})
    return {
        sortList: data.data
    }
}

export default Index;
