import ArticleList from '../components/ArticleList';
import Head from 'next/head'

const Index = ({ count, add ,bdd}) => {
    console.log(count, add ,bdd ,'===');
    return (
        <div className="homeWrap">
            <Head>
                <title>首页</title>
            </Head>
            <div onClick={add}>11111</div>
            <div onClick={bdd}>22222</div>
