                    <ArticleList />
                </div>
                <div className="homeRight">
                    <LeavingMessage />
                    <VisitorList />
                </div>
            <style jsx>
            {`
                .homeWrap{
                    display: flex;
                    max-width: 1200px;
                    width: 1200px;
                    padding: 20px 0;
                    margin:0 auto;
