import { Modal, Button, Input } from "antd";
import React from 'react';
import httpAgent from '../../httpAgent';

class LocalizedModal extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            visible: this.props.visible,
            user_ip: this.props.user_ip
        }
    }
    showModal = () => {
        this.setState({
            visible: true,
        });

    };

    hideModal = () => {
        this.setState({
            visible: false,
        });

        const params = {
            ip: this.state.user_ip,
            email: '',
            nickname: ''
        };
        httpAgent({url: '/user/addUser', method: 'post' , params}).then(res => {
            this.setState({
                visible: false
            })
        })
    };
    componentWillReceiveProps(nextProps) {
        const { visible } = this.state;
        const newdata = nextProps.visible.toString()
        if (visible.toString() !== newdata) {
            this.setState({
                visible: nextProps.visible,
            })
        }
    }
    render() {
        return (
        <>
            <Modal
                title="Modal"
                visible={this.state.visible}
                onOk={this.hideModal}
                onCancel={this.hideModal}
                okText="确认"
                cancelText="取消"
            >
                <Input className="inputs" placeholder="email" onChange={this.inputChange} value={this.state.email}/>
                <Input className="inputs" placeholder="nickname" onChange={this.inputChange} value={this.state.nickname}/>       
            </Modal>
        </>
        );
    }
}
export default LocalizedModal;
