    return (
        <div className="sm:mx-0">
        {slug ? (
            <Link as={`/posts/${slug}`} href="/posts/[slug]">
            <a aria-label={title}>{image}</a>
            </Link>
        ) : (
            <img
            src={src}
            alt={`Cover Image for ${title}`}
            className={cn('shadow-small', {
                'hoverCover': slug,
            })}
            />
        )}
            <style jsx>
                {`
                    .shadow-small{
                        width: 960px;
                    }
                `}
            </style>
        </div>
    )
}
