const httpAgent = async ({
    url, method, params
}) => {
    function getBrowserInfo() {
        var agent = navigator.userAgent.toLowerCase();

        var regStr_ie = /msie [\d.]+;/gi;
        var regStr_ff = /firefox\/[\d.]+/gi;
        var regStr_chrome = /chrome\/[\d.]+/gi;
        var regStr_saf = /safari\/[\d.]+/gi;
        //IE
        if (agent.indexOf("msie") > 0) {
        return agent.match(regStr_ie);
        }

        //firefox
        if (agent.indexOf("firefox") > 0) {
        return agent.match(regStr_ff);
        }

        //Safari
        if (agent.indexOf("safari") > 0 && agent.indexOf("chrome") < 0) {
        return agent.match(regStr_saf);
        }

        //Chrome
        if (agent.indexOf("chrome") > 0) {
        return agent.match(regStr_chrome);
        }
    }
    //（2）然后获取版本号
    var browser = getBrowserInfo(); //返回的是个长度为1的数组，根据自己的需要进行转换
    var verinfo = (browser + "").replace(/[^0-9.]/gi, "");

    console.log(browser, verinfo, 'verinfoverinfoverinfoverinfo');

    const res = await fetch('http://127.0.0.1:7000/api/agent', {
        headers: {
          'content-type': 'application/json',
          'api-url': url,
          'api-method': method
        },
        method: 'POST',
        body: JSON.stringify(params),
    });
    return await res.json();
}

export default httpAgent
