import { createStore, applyMiddleware } from "redux";
import ReduxThunk from "redux-thunk";
import indexState from '../store/index';
import postState from '../store/post';
import comState from '../store/com';

const allState = Object.assign(indexState, postState, comState);
const initialState = Object.keys(allState).map(key => {
    return {
        [key]: allState[key]()['default']
    }
})
console.log(initialState);
// const initialState = {
//     labelPage: 0,
//     indexPage: {
//         articleData: {}
//     }
// };

function reduxHandle({ type, action }, fn) {

    return fn()
}

function reducer(state = initialState, action) {

    if (action.type === 'handleLabelPage') {
        return {
            labelPage: Number(action.labelPage)
        };
    }

    if (action.type === 'testHandle') {
        return {
            indexPage: {
                articleData: action
            }
        };
    }

    return state;
}

// 这里暴露出的是创建store的工厂方法
// 每次渲染都需要重新创建一个store实例
// 防止服务端一直复用旧实例 无法和客户端状态同步
export default function initializeStore(state) {

    const store = createStore(
        reducer,
        Object.assign({}, initialState, state),
        applyMiddleware(ReduxThunk)
    )
    return store;
}
