            .homeRight li{
                margin-bottom: 5px;
            }
            .visitorTitle{
                font-size: 12px;
                color: #333333;
                margin-bottom: 10px;
            }
            .visitorInfo span{
                font-size: 12px;
                color: #666666;
                margin-right: 10px;
            }
        `}
        </style>
    </div>
);

Index.getInitialProps = async () => {
    const data = await http({ url: '/sort/getSortList', method: 'get' , params: {}})
    return {
        sortList: data.data
    }
}

export default Index;
