    //         return {
    //             indexPage: {
    //                 articleData: action.data
    //             }
    //         };
    //     }

    // })
    // return reduxState;
    getA('handleLabelPage')
    function getA(handleLabelPage) {
        if (action.type === handleLabelPage) {
            return {
                labelPage: Number(action.labelPage)
            };
        }
    }

    // if (action.type === 'handleLabelPage') {
    //     return {
    //         labelPage: Number(action.labelPage)
    //     };
    // }

    if (action.type === 'testHandle') {
        return {
            indexPage: {
                articleData: action.data
            }
        };
    }

    return state;
}

// 这里暴露出的是创建store的工厂方法
// 每次渲染都需要重新创建一个store实例
// 防止服务端一直复用旧实例 无法和客户端状态同步
export default function initializeStore(state) {

    const store = createStore(
        reducer,
        Object.assign({}, initialState, state),
        applyMiddleware(ReduxThunk)
    )
    return store;
}
