    <div>
        <Head>
            <title>首页</title>
        </Head>
        <div class="homeWrap">
            <div className="homeLeft">
    1
            </div>
            <div className="homeRight">
    2
            </div>
        </div>
        <style jsx>
        {`
            .homeWrap{
                display: flex;
                max-width: 1200px;
                width: 1200px;
            }
            .homeLeft{
                width: 800px;
            }
        `}
        </style>
    </div>
);

Index.getInitialProps = async () => {
    const data = await http({ url: '/sort/getSortList', method: 'get' , params: {}})
    return {
        sortList: data.data
    }
}

export default Index;
