import Avatar from '../components/others/avatar';
import Comment from '../components/Comment';
import httpAgent from '../httpAgent';
import { render } from 'sass';

// export default function PostBody({ content, au_in_for, user_ip, com_user_info, slug }) {
export default class PostBody extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            content: this.props.content, 
            au_in_for: this.props.au_in_for, 
            user_ip: this.props.user_ip, 
            com_user_info: this.props.com_user_info, 
            slug: this.props.slug,
            length: 0,
            commentList: []
        }
    }
    componentDidMount() {
        httpAgent({url: '/message/getCommentList', method: 'post' , params: {
            articleId: this.state.slug
        }}).then(res => {
            let length = res.data.length;
            res.data && res.data.forEach(element => {
                length = length + element.child.length
            });
            this.setState({
                length,
                commentList: res.data
            })
        })
    }
    render() {
        const { au_in_for, content, user_ip, com_user_info, slug, commentList, length } = this.state;
        const { title } = au_in_for;
        return (
            <div>
                <div className="md-editor-container">
                    <h1>{title}</h1>
                    <Avatar au_in_for={au_in_for} length={length}/>
                    <div
                        className="preview-container"
                        dangerouslySetInnerHTML={{ __html: content }}
                    />
                </div>
                <Comment commentList={commentList} length={length} slug={slug} user_ip={user_ip} com_user_info={com_user_info}/>
                <style jsx>
                    {
                        `
                        h1{
                            font-size: 30px;
                            font-weight: 700;
                            word-break: break-word;
                            color: #404040;
                            margin-bottom: .5em;
                            line-height: 1.42857;
                        }
                        ` 
                    }
                </style>
            </div>
        )
    }
}
