import styles from '../../styles/articleTrees.module.scss';
import { Tree } from 'antd';
import { isEmpty } from 'zgl-utils-js';
import React, { useState } from 'react';
import httpAgent from '../../httpAgent';


const { DirectoryTree } = Tree;

export default function ArticleTrees ({com_class_list}) {

    const  com_classList = !isEmpty(com_class_list) && com_class_list.map(item => {
         if (item.isLeaf) {
             return {
                 title: item.name,
                 key: item.value
             }
         } else {
             return {
                 title: item.name,
                 key: item.value,
                 children: getItemChild(item.children)
             }
         }
     })
     const [treeData, setTreeData] = useState(com_classList)

    function getItemChild(item) {
        return item.map(child => {
            if (child.isLeaf) {
                return {
                    title: child.name,
                    key: child.value
                }
            } else {
                return {
                    title: child.name,
                    key: child.value,
                    children: getItemChild(child.children)
                }
            }
        })
    }
    function updateTreeData (list, key, children) {
        return list.map((node) => {
          if (node.key === key) {
            return { ...node, children };
          } else if (node.children) {
            return { ...node, children: updateTreeData(node.children, key, children) };
          }

          return node;
        });
    }
    function onSelect(keys, event) {
        console.log('Trigger Select', keys, event);
    };

    function onExpand() {
        console.log('Trigger Expand');
    };
    function onLoadData({ key, children }) {
        console.log(children, 'childrenchildren');
        // /article/getArticleList
        return new Promise((resolve) => {
            if (children) {
                resolve();
                return;
            }
            setTimeout(() => {
                setTreeData((origin) =>
                    updateTreeData(origin, key, [
                        {
                            title: 'Child Node',
                            key: `${key}-0`,
                            isLeaf: true
                        },
                        {
                            title: 'Child Node',
                            key: `${key}-1`,
                            isLeaf: true
                        },
                    ]),
                );
                resolve();
            }, 1000);
        });
    }


    return (
        <div className={styles.trees}>
            <div className={styles.visitorTitle}> 
                分类列表 
            </div>
            <DirectoryTree
                multiple
                loadData={onLoadData}
                onSelect={onSelect}
                onExpand={onExpand}
                treeData={treeData}
            />
        </div>
    )
}
